    }
  return (
    <div className='questions'>
        <h2 className='text-light'>simple question</h2>
        <ul>
                <li>
                        <input 
                            type="radio"
                            value={checked}
                            name="options"
                            id='q1-option'
                            onChange={onSelect()}
                            />
                            </li>
                            </ul>

        </div>
  )
}
